mapper.select:
select transaction_id, from_user_id, (Select user_name from sys_user where from_user_id = user_id) fromUserName, from_wallet_address, to_user_id, (Select user_name from sys_user where to_user_id = user_id) toUserName, to_wallet_address, create_time, remark, from_amount, from_wallet_type, to_amount, to_wallet_type, transaction_type, fee_amount, case transaction_status when '-1' then '交易失败' when '0' then '交易中' when '1' then '交易成功' when '-2' then '审核不通过' end transactionStatus, transaction_status, transaction_hash from mdc_transaction where 0 = 0
and from_user_id in (select user_id from sys_user where user_name like #{_fromUserName})
and transaction_type=#{transactionType}
and transaction_type in ('0','1')
and transaction_status=#{transactionStatus}
and create_time >= #{startTime}
and create_time <= #{endTime}
and to_wallet_address like #{_toWalletAddress}
and (from_wallet_address=#{walletAddress} or to_wallet_address=#{walletAddress})
and to_wallet_address like #{_investAddress}
and to_wallet_type = #{invest_wallet_type}
and to_user_id in (Select user_id from sys_user where user_name like #{_investUserName})
and from_wallet_address like #{_cashOutAddress}
and from_wallet_type = #{cashOut_wallet_type}
and to_wallet_type = #{toWalletType}
and from_user_id in (Select user_id from sys_user where user_name like #{_cashOutUserName})
order by create_time desc

select transaction_id, from_user_id, (Select user_name from sys_user where from_user_id = user_id) fromUserName, from_wallet_address, to_user_id, (Select user_name from sys_user where to_user_id = user_id) toUserName, to_wallet_address, create_time, remark, from_amount, from_wallet_type, case from_wallet_type when '0' then 'USDT' when '1' then 'MDC' end walletType, to_amount, to_wallet_type, transaction_type, fee_amount, case transaction_status when '-1' then '交易失败' when '0' then '交易中' when '1' then '交易成功' when '-2' then '审核不通过' end transactionStatus, transaction_status, transaction_hash from mdc_transaction where 0 = 0
and from_user_id in (select user_id from sys_user where user_name like #{_fromUserName})
and transaction_type=#{transactionType}
and transaction_type in ('0','1')
and transaction_status=#{transactionStatus}
and create_time >= #{startTime}
and create_time <= #{endTime}
and to_wallet_address like #{_toWalletAddress}
and (from_wallet_address=#{walletAddress} or to_wallet_address=#{walletAddress})
and to_wallet_address like #{_investAddress}
and to_wallet_type = #{invest_wallet_type}
and to_user_id in (Select user_id from sys_user where user_name like #{_investUserName})
and from_wallet_address like #{_cashOutAddress}
and from_wallet_type = #{cashOut_wallet_type}
and to_wallet_type = #{toWalletType}
and from_user_id in (Select user_id from sys_user where user_name like #{_cashOutUserName})
order by create_time desc

select transaction_id, from_user_id, (Select user_name from sys_user where from_user_id = user_id) fromUserName, from_wallet_address, to_user_id, (Select user_name from sys_user where to_user_id = user_id) toUserName, to_wallet_address, create_time, remark, from_amount, from_wallet_type, case to_wallet_type when '0' then 'USDT' when '1' then 'MDC' end walletType, to_amount, to_wallet_type, transaction_type, case transaction_type when '0' then '充值' when '1' then '提现' end transactionType, fee_amount, case transaction_status when '-1' then '交易失败' when '0' then '交易中' when '1' then '交易成功' when '-2' then '审核不通过' end transactionStatus, transaction_status, transaction_hash from mdc_transaction where 0 = 0
and from_user_id in (select user_id from sys_user where user_name like #{_fromUserName})
and transaction_type=#{transactionType}
and transaction_type in ('0','1')
and transaction_status=#{transactionStatus}
and create_time >= #{startTime}
and create_time <= #{endTime}
and to_wallet_address like #{_toWalletAddress}
and (from_wallet_address=#{walletAddress} or to_wallet_address=#{walletAddress})
and to_wallet_address like #{_investAddress}
and to_wallet_type = #{invest_wallet_type}
and to_user_id in (Select user_id from sys_user where user_name like #{_investUserName})
and from_wallet_address like #{_cashOutAddress}
and from_wallet_type = #{cashOut_wallet_type}
and to_wallet_type = #{toWalletType}
and from_user_id in (Select user_id from sys_user where user_name like #{_cashOutUserName})
order by create_time desc

select t.transaction_id, t.from_user_id, t.from_wallet_address, t.to_user_id, t.to_wallet_address, t.create_time, t.remark, t.from_amount, t.from_wallet_type, t.to_amount, t.to_wallet_type, t.transaction_type, t.fee_amount, t.transaction_status, t.transaction_hash, t.contract_type, (SELECT user_name from sys_user where user_id = u.user_id) fromUserName, c.name contractName, u.contract_id, case contract_type when '-1' then '解约' when '1' then '购买' when '2' then '进阶' end contractType, case t.transaction_status when '-1' then '交易失败' when '0' then '交易中' when '1' then '交易成功' when '-2' then '审核不通过' end transactionStatus, from mdc_transaction t,(select max(create_time),user_id,contract_id from mdc_user_contract where del_flag=0 group by user_id,contract_id) u,sys_contract c where transaction_type = '4' and t.from_user_id = u.user_id and u.contract_id = c.id
and t.contract_type = #{contractType}
and c.name = #{contractName}
and from_user_id in (Select user_id from sys_user where user_name like #{_userName})
and t.create_time >= #{startTime}
and t.create_time <= #{endTime}
order by t.create_time desc
